package com.example.realestate.model.dao;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;import com.example.realestate.model.entity.Estate;import com.example.realestate.model.entity.Image;import java.util.List;@Daopublic interface ImageDao {    @Insert    void insert(Image image);    @Delete    void delete(Image image);    @Update    void update(Image image);    @Query("select * from Image where estateID=:estateID")    LiveData<List<Image>> getByEstateID(int estateID);}