package com.example.realestate.model.dao;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;import com.example.realestate.model.entity.Image;import com.example.realestate.model.entity.Location;import java.util.List;@Daopublic interface LocationDao {    @Insert    void insert(Location location);    @Delete    void delete(Location location);    @Update    void update(Location location);    @Query("select * from Location")    LiveData<List<Location>> getAllLocation();}