package com.example.realestate.model.dao;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;import com.example.realestate.model.entity.Office;import java.util.List;@Daopublic interface OfficeDao {    @Insert    void insert(Office office);    @Delete    void delete(Office office);    @Update    void update(Office office);    @Query("select * from Office")    LiveData<List<Office>> getAllOffice();    @Query("select * from Office where NumberPhone=:numberPhone")    LiveData<Office> getOfficeByNumberPhobe(String numberPhone);}