package com.example.realestate.model.dao;import android.graphics.Path;import androidx.lifecycle.LiveData;import androidx.room.Dao;import androidx.room.Delete;import androidx.room.Insert;import androidx.room.Query;import androidx.room.Update;import com.example.realestate.model.entity.Office;import com.example.realestate.model.entity.Opration;import java.util.List;@Daopublic interface OprationDao {    @Insert    void insert(Opration opration);    @Delete    void delete(Opration opration);    @Update    void update(Opration opration);    @Query("select * from Opration")    LiveData<List<Opration>> getAllOpration();}