package com.example.realestate.model.database;import android.content.Context;import androidx.room.Database;import androidx.room.RoomDatabase;import com.example.realestate.model.dao.EstateDao;import com.example.realestate.model.dao.ImageDao;import com.example.realestate.model.dao.LocationDao;import com.example.realestate.model.dao.OfficeDao;import com.example.realestate.model.dao.OprationDao;import com.example.realestate.model.dao.TypeEstateDao;import com.example.realestate.model.entity.Estate;import com.example.realestate.model.entity.Image;import com.example.realestate.model.entity.Location;import com.example.realestate.model.entity.Office;import com.example.realestate.model.entity.Opration;import com.example.realestate.model.entity.TypeEstate;@Database(entities =        {                Estate.class,                Image.class,                Location.class,                Office.class,                Opration.class,                TypeEstate.class        }, version = 1)public abstract class RealEstateDateBase extends RoomDatabase {    private static volatile RealEstateDateBase instance;    public static RealEstateDateBase getDatabase(final Context context) {        if (instance == null) {            synchronized (RealEstateDateBase.class) {                if (instance == null) {                    instance = androidx.room.Room.databaseBuilder( context.getApplicationContext(),                            RealEstateDateBase.class, "RealEstateDb" )                            .build();                }            }        }        return instance;    }    public abstract EstateDao getEstateDao();    public abstract ImageDao getImageDao();    public abstract LocationDao getLocationDao();    public abstract OfficeDao getOfficeDao();    public abstract OprationDao getOprationDao();    public abstract TypeEstateDao getTypeEstateDao();}