package com.example.realestate.model.entity;import androidx.room.ColumnInfo;import androidx.room.Entity;import androidx.room.PrimaryKey;@Entity(tableName = "Location")public class Location {    @PrimaryKey(autoGenerate = true)    @ColumnInfo(name = "ID")    private int id;    @ColumnInfo(name = "Name")    private String name;    public Location(int id, String name) {        this.id = id;        this.name = name;    }    public Location() {    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }}