package com.example.realestate.model.entity;import androidx.room.ColumnInfo;import androidx.room.Entity;import androidx.room.PrimaryKey;@Entity(tableName = "Opration")public class Opration {    @PrimaryKey(autoGenerate = true)    @ColumnInfo(name = "ID")    private int id;    @ColumnInfo(name = "Name")    private String name;    @ColumnInfo(name = "Color")    private String color;    @ColumnInfo(name = "Icon", typeAffinity = ColumnInfo.BLOB)    private byte[] icon;    public Opration(int id, String name, String color, byte[] icon) {        this.id = id;        this.name = name;        this.color = color;        this.icon = icon;    }    public Opration() {    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getColor() {        return color;    }    public void setColor(String color) {        this.color = color;    }    public byte[] getIcon() {        return icon;    }    public void setIcon(byte[] icon) {        this.icon = icon;    }}