package com.example.realestate.util;import android.app.Application;import android.content.res.Configuration;import android.content.res.Resources;import android.os.Build;import android.util.DisplayMetrics;import java.util.Locale;import io.paperdb.Paper;public class RealEstateApplication extends Application {    @Override    public void onCreate()    {        super.onCreate();        Paper.init(this);    }    private void setAppLocal(String  localCode) {        Resources res = getResources();        DisplayMetrics dm = res.getDisplayMetrics();        Configuration configuration = res.getConfiguration();        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.JELLY_BEAN_MR1) {            configuration.setLocale( new Locale( localCode.toLowerCase() ) );        } else {            configuration.locale = new Locale( localCode.toLowerCase() );        }        res.updateConfiguration( configuration, dm );    }}