package com.example.realestate.view.activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.view.animation.Animation;import android.view.animation.AnimationUtils;import android.widget.ImageView;import androidx.appcompat.app.AppCompatActivity;import androidx.cardview.widget.CardView;import com.example.realestate.R;public class OprationActivity extends AppCompatActivity {    private Animation topAnimation;    private Animation bottomAnimation;    private ImageView imgIconApp;    private CardView cardSale;    private CardView cardLeasing;    private CardView cardSwap;    private CardView cardMortgage;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate( savedInstanceState );        setContentView( R.layout.activity_opration );        topAnimation = AnimationUtils.loadAnimation( this, R.anim.top_animtion );        bottomAnimation = AnimationUtils.loadAnimation( this, R.anim.bottom_animation );        imgIconApp = findViewById( R.id.imgIconApp );        cardSale = findViewById( R.id.cardSale );        cardLeasing = findViewById( R.id.cardLeasing );        cardSwap = findViewById( R.id.cardSwap );        cardMortgage = findViewById( R.id.cardMortgage );        imgIconApp.setAnimation( topAnimation );        cardSale.setAnimation( bottomAnimation );        cardLeasing.setAnimation( bottomAnimation );        cardSwap.setAnimation( bottomAnimation );        cardMortgage.setAnimation( bottomAnimation );        Intent intent = new Intent( OprationActivity.this, HomeActivity.class );        cardSale.setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                intent.putExtra( "cardType", "Sale" );                startActivity( intent );            }        } );        cardLeasing.setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                intent.putExtra( "cardType", "Leasing" );                startActivity( intent );            }        } );        cardSwap.setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                intent.putExtra( "cardType", "Swap" );                startActivity( intent );            }        } );        cardMortgage.setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                intent.putExtra( "cardType", "Mortgage" );                startActivity( intent );            }        } );    }}