package com.example.realestate.view.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import androidx.viewpager2.widget.ViewPager2;import com.example.realestate.R;import com.example.realestate.model.entity.SlideImage;import com.example.realestate.view.adapter.viewholder.SliderViewHolder;import java.util.List;public class SliderAdapter extends RecyclerView.Adapter<SliderViewHolder> {    private ViewPager2 viewPager2;    private List<SlideImage>slideImages;    public SliderAdapter(ViewPager2 viewPager2, List<SlideImage> slideImages) {        this.viewPager2 = viewPager2;        this.slideImages = slideImages;    }    @NonNull    @Override    public SliderViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from( parent.getContext() ).inflate( R.layout.item_slid_container, parent, false );        return new SliderViewHolder( view );    }    @Override    public void onBindViewHolder(@NonNull SliderViewHolder holder, int position) {        holder.setImage( slideImages.get( position ) );        if (position==slideImages.size()-2){            viewPager2.post( runnable );        }    }    @Override    public int getItemCount() {        return slideImages.size();    }    private Runnable runnable=new Runnable() {        @Override        public void run() {            slideImages.addAll( slideImages );            notifyDataSetChanged();        }    };}