package com.example.realestate.view.adapter.viewholder;import android.view.View;import android.widget.ImageView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.realestate.R;import com.example.realestate.model.entity.SlideImage;public class SliderViewHolder extends RecyclerView.ViewHolder {    private View view;    private ImageView imageSlide;    public SliderViewHolder(@NonNull View view) {        super( view );        this.view=view;        imageSlide=view.findViewById( R.id.imageSlide );    }    public void setImage(SlideImage slideImage){        imageSlide.setImageResource( slideImage.getImage() );    }}