package com.example.realestate.view.dialog;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.EditText;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import com.example.realestate.R;import com.example.realestate.model.entity.TypeEstate;import com.example.realestate.view.activity.HomeActivity;import com.google.android.material.bottomsheet.BottomSheetDialogFragment;import br.com.simplepass.loadingbutton.customViews.CircularProgressButton;public class TypeEstateBottomSheet extends BottomSheetDialogFragment {    private EditText edtBottomSheetTypeEstateName;    private CircularProgressButton btnBottomSheetTypeEstateSave;    private TypeEstate currentTypeEstate = null;    public TypeEstateBottomSheet(TypeEstate typeEstate) {        this.currentTypeEstate = typeEstate;    }    @Nullable    @Override    public View onCreateView(@NonNull LayoutInflater inflater,                             @Nullable ViewGroup container,                             @Nullable Bundle savedInstanceState) {        View view = inflater.inflate( R.layout.bottom_sheet_tye_estate, container );        edtBottomSheetTypeEstateName = view.findViewById( R.id.edtBottomSheetTypeEstateName );        btnBottomSheetTypeEstateSave = view.findViewById( R.id.btnBottomSheetTypeEstateSave );        if (currentTypeEstate != null) {            edtBottomSheetTypeEstateName.setText( currentTypeEstate.getName() );        }        btnBottomSheetTypeEstateSave.setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                String name = edtBottomSheetTypeEstateName.getText().toString().trim();                if (name.equals( "" )) {                    edtBottomSheetTypeEstateName.setError( "Faild can't be Empty" );                    return;                }                if (currentTypeEstate == null) {                    ((HomeActivity) getActivity()).typeEstateinset( new TypeEstate( 0, name ) );                } else {                    currentTypeEstate.setId( currentTypeEstate.getId() );                    currentTypeEstate.setName( name );                    ((HomeActivity) getActivity()).typeEstateupdate( currentTypeEstate );                }                dismiss();            }        } );        return view;    }}