package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.Estate;import com.example.realestate.model.entity.EstateInfo;import com.example.realestate.repository.EstateRepository;import java.util.List;public class EstateViewModel extends AndroidViewModel {    private EstateRepository estateRepository;    public EstateViewModel(@NonNull Application application) {        super( application );        estateRepository = new EstateRepository( application );    }    public void insert(Estate estate) {        estateRepository.insert( estate );    }    public void delete(Estate estate) {        estateRepository.delete( estate );    }    public void deleteByID(int ID) {        estateRepository.deleteByID( ID );    }    public void update(Estate estate) {        estateRepository.update( estate );    }    public LiveData<List<Estate>> getAllByEstateTypeID(int TypeEstateID, String OprationName) {        return estateRepository.getAllByEstateTypeID( TypeEstateID, OprationName );    }    public LiveData<List<EstateInfo>> getEsateInfo(String oprationName, int typeEstateID) {        return estateRepository.getEsateInfo( oprationName, typeEstateID );    }    public LiveData<List<EstateInfo>> getMyEstate(int officeID) {        return estateRepository.getMyEstate( officeID );    }    public LiveData<Integer> getEstateID() {        return estateRepository.getEstateID();    }    public LiveData<Estate>getEstateByID(int id ){        return estateRepository.getEstateByID(id);    }}