package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.Image;import com.example.realestate.repository.ImageRepository;import java.util.List;public class ImageViewModel extends AndroidViewModel {    private ImageRepository imageRepository;    public ImageViewModel(@NonNull Application application) {        super( application );        imageRepository = new ImageRepository( application );    }    public void insert(List<Image> images) {        imageRepository.insert( images );    }    public void insert(Image image) {        imageRepository.insert( image );    }    public void update(Image image) {        imageRepository.update( image );    }    public void delete(Image image) {        imageRepository.delete( image );    }    public LiveData<List<Image>> getByEstateID(int estateID) {        return imageRepository.getByEstateID( estateID );    }}