package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.Location;import com.example.realestate.repository.LocationRepository;import java.util.List;public class LocationViewModel extends AndroidViewModel {    private LocationRepository locationRepository;    public LocationViewModel(@NonNull Application application) {        super( application );        locationRepository = new LocationRepository( application );    }    public void insert(Location location) {        locationRepository.insert( location );    }    public void update(Location location) {        locationRepository.update( location );    }    public void delete(Location location) {        locationRepository.delete( location );    }    public LiveData<List<Location>> getAllLocation() {        return locationRepository.getAllLocation();    }}