package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.Office;import com.example.realestate.repository.OfficeRepository;import java.util.List;public class OfficeViewModel extends AndroidViewModel {    private OfficeRepository officeRepository;    public OfficeViewModel(@NonNull Application application) {        super( application );        officeRepository = new OfficeRepository( application );    }    public void insert(Office office) {        officeRepository.insert( office );    }    public void update(Office office) {        officeRepository.update( office );    }    public void delete(Office office) {        officeRepository.delete( office );    }    public LiveData<List<Office>> getAllOffice() {        return officeRepository.getAllOffice();    }    public LiveData<Office> getOfficeByNumberPhone(String numberPhone) {        return officeRepository.getofficeByNumberPhone( numberPhone );    }}