package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.Opration;import com.example.realestate.repository.OprationRepository;import java.util.List;public class OprationViewModel extends AndroidViewModel {    private OprationRepository oprationRepository;    public OprationViewModel(@NonNull Application application) {        super( application );        oprationRepository = new OprationRepository( application );    }    public void insert(Opration opration) {        oprationRepository.insert( opration );    }    public void update(Opration opration) {        oprationRepository.update( opration );    }    public void delete(Opration opration) {        oprationRepository.delete( opration );    }    public LiveData<List<Opration>> getAllOpration() {        return oprationRepository.getAllOpration();    }}