package com.example.realestate.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.example.realestate.model.entity.TypeEstate;import com.example.realestate.repository.TypeEstateRepository;import java.util.List;public class TypeEstateViewModel extends AndroidViewModel {    private TypeEstateRepository typeEstateRepository;    public TypeEstateViewModel(@NonNull Application application) {        super( application );        typeEstateRepository = new TypeEstateRepository( application );    }    public void insert(TypeEstate typeEstate) {        typeEstateRepository.insert( typeEstate );    }    public void update(TypeEstate typeEstate) {        typeEstateRepository.update( typeEstate );    }    public void delete(TypeEstate typeEstate) {        typeEstateRepository.delete( typeEstate );    }    public LiveData<List<TypeEstate>> getAllTypeEstate() {        return typeEstateRepository.getAllTypeEstate();    }}